import React from 'react';
import './AboutPage.css'; // Import a CSS file for styling (optional)
import Navbar from '../../components/Navbar';

const AboutPage = () => {
    <Navbar/>
  return (
    <div className="about-container">
      <div className="about-header">
        <h1 className="about-title">About Us</h1>
      </div>
      
      <div className="about-content">
        <p className="about-subheading">Our Mission</p>
        <p className="about-description">
          At ShopMart, our mission is to provide high-quality products with exceptional customer service. We are committed to delivering the best shopping experience, both online and in-store.
        </p>

        <p className="about-subheading">Our Vision</p>
        <p className="about-description">
          Our vision is to be the go-to destination for all your shopping needs, offering a wide range of products while keeping our values of trust, convenience, and customer satisfaction at the core of everything we do.
        </p>
        
        <button className="explore-button">Explore More</button>
      </div>
    </div>
  );
};

export default AboutPage;
