import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Pro from './pro';

const Prod = () => {
    const [product, setProduct] = useState([]);

    // Function to fetch products
    const getProduct = () => {
        axios.get('https://api.escuelajs.co/api/v1/products/20') // Updated to fetch all products
            .then(response => setProducts(response.data))  // Set fetched products to state
            .catch(error => console.log(error));  // Handle any errors
    };

    // Use useEffect to call getProducts when the component mounts
    useEffect(() => {
        getProducts();
    }, []);

    return (
        <div className="container">
            <h2 className="text-center">Products</h2>
            <div className="row">
                {
                    product.map((product, index) => (
                        <Pro key={index} data={product} />
                    ))
                }
            </div>
        </div>
    );
};

export default Prod;
