import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

const ProductDetail = () => {
    const id = 4;
    const [product, setProduct] = useState({});

    // Function to fetch product details
    const getProduct = async () => {
        try {
            // Use template literals for string interpolation
            const response = await axios.get(`https://api.escuelajs.co/api/v1/products/${id}`);
            setProduct(response.data);
        } catch (error) {
            console.log(error);  // Handle any errors
        }
    };

    // Use useEffect to call getProduct when the component mounts
    useEffect(() => {
        getProduct();
    }, [id]);

    return (
        <div className="container">
            <h2 className="text-center">Product</h2>
            <div className="row">
                <div className="col-md-6">
                    <div className="wrapper">
                        {/* Ensure the product image URL is correct */}
                        <img src={product.images ? product.images[0] : ''} alt={product.title} className="img-fluid" />
                    </div>
                </div>
                <div className="col-md-6">
                    <div className="wrapper">
                        <h5 className="card-title">{product.title}</h5>
                        <p className="card-text">
                            <span className="badge badge-secondary">{product.category?.name}</span>
                        </p>
                        <h2>
                            <span>&#8377;</span>{product.price}
                        </h2>
                        <a href="#" className="btn btn-primary">
                            Add to cart
                        </a>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
