import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductDetail = () => {
    const [products, setProducts] = useState([]);  // Using array for product (even if it's a single product)
    
    // Fetching product data (single or multiple)
    const getProduct = () => {
        axios.get('https://api.escuelajs.co/api/v1/products/1')  // Update with your product ID or URL
            .then(response => setProducts([response.data]))  // Store product as an array
            .catch(error => console.log(error));  // Handle error
    };

    // Using useEffect to fetch product data when the component mounts
    useEffect(() => {
        getProduct();
    }, []);

    return (
        <div>
            <h1>Product Details</h1>
            {/* Using .map() to iterate over products (even if it's just one) */}
            {products.map(product => (
                <div key={product.id}>
                    <h2>{product.title}</h2> {/* Display product title */}
                    <img src={product.images[0]} alt={product.title} style={{ width: '200px' }} /> {/* Display product image */}
                    <p><strong>Price:</strong> ${product.price}</p> {/* Display product price */}
                    <p><strong>Description:</strong> {product.description}</p> {/* Display product description */}
                </div>
            ))}
        </div>
    );
};

export default Prod;
