import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CategoryList = () => {
    const [categories, setCategories] = useState([]);

    // Function to fetch categories
    const getCategories = () => {
        axios.get('https://api.escuelajs.co/api/v1/categories')
            .then(response => setCategories(response.data))  // Set fetched categories to state
            .catch(error => console.log(error));  // Handle any errors
    };

    // Use useEffect to call getCategories when the component mounts
    useEffect(() => {
        getCategories();
    }, []);

    return (
        <div>
            <h1>Category List</h1>
            <ul>
                {categories.map((category) => (
                    <li key={category.id}>{category.name}</li>  // Assuming each category has an 'id' and 'name'
                ))}
            </ul>
        </div>
    );
}

export default CategoryList;
